import { TextEncoder } from "util";
import { encode, encodeUrl, decode } from "./index";

const encoder = new TextEncoder();
const rangeBuffer = new Uint8Array(Array.from({ length: 256 }, (_, i) => i));

describe("base64", () => {
  it("should encode base64", () => {
    expect(encode(encoder.encode("hello"))).toEqual("aGVsbG8=");
    expect(encode(encoder.encode("你好"))).toEqual("5L2g5aW9");

    expect(encode(rangeBuffer)).toEqual(
      ""
    );
  });

  it("should encode base64url", () => {
    expect(encodeUrl(encoder.encode("hello"))).toEqual("aGVsbG8");

    expect(encodeUrl(rangeBuffer)).toEqual(
      "AAECAwQFBgcICQoLDA0ODxAREhMUFRYXGBkaGxwdHh8gISIjJCUmJygpKissLS4vMDEyMzQ1Njc4OTo7PD0-P0BBQkNERUZHSElKS0xNTk9QUVJTVFVWV1hZWltcXV5fYGFiY2RlZmdoaWprbG1ub3BxcnN0dXZ3eHl6e3x9fn-AgYKDhIWGh4iJiouMjY6PkJGSk5SVlpeYmZqbnJ2en6ChoqOkpaanqKmqq6ytrq-wsbKztLW2t7i5uru8vb6_wMHCw8TFxsfIycrLzM3Oz9DR0tPU1dbX2Nna29zd3t_g4eLj5OXm5-jp6uvs7e7v8PHy8_T19vf4-fr7_P3-_w"
    );
  });

  it("should decode base64", () => {
    expect(decode("aGVsbG8=").buffer).toEqual(encoder.encode("hello").buffer);
    expect(decode("5L2g5aW9").buffer).toEqual(encoder.encode("你好").buffer);

    expect(
      decode(
        ""
      ).buffer
    ).toEqual(rangeBuffer.buffer);
  });

  it("should decode base64url", () => {
    expect(decode("aGVsbG8").buffer).toEqual(encoder.encode("hello").buffer);

    expect(
      decode(
        "AAECAwQFBgcICQoLDA0ODxAREhMUFRYXGBkaGxwdHh8gISIjJCUmJygpKissLS4vMDEyMzQ1Njc4OTo7PD0-P0BBQkNERUZHSElKS0xNTk9QUVJTVFVWV1hZWltcXV5fYGFiY2RlZmdoaWprbG1ub3BxcnN0dXZ3eHl6e3x9fn-AgYKDhIWGh4iJiouMjY6PkJGSk5SVlpeYmZqbnJ2en6ChoqOkpaanqKmqq6ytrq-wsbKztLW2t7i5uru8vb6_wMHCw8TFxsfIycrLzM3Oz9DR0tPU1dbX2Nna29zd3t_g4eLj5OXm5-jp6uvs7e7v8PHy8_T19vf4-fr7_P3-_w"
      ).buffer
    ).toEqual(rangeBuffer.buffer);
  });
});
